import { Component, OnInit } from '@angular/core';
import { trigger, state, style, transition, animate, keyframes } from '@angular/animations';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  animations: [
    trigger('fadeIn', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(50px)' }),
        animate('1.2s cubic-bezier(0.35, 0, 0.25, 1)', style({ opacity: 1, transform: 'translateY(0)' }))
      ])
    ]),
    trigger('heartBeat', [
      state('beat', style({ transform: 'scale(1)' })),
      transition('* => beat', [
        animate('1.2s cubic-bezier(0.36, 0, 0.66, -0.56)', keyframes([
          style({ transform: 'scale(1)', offset: 0 }),
          style({ transform: 'scale(1.15)', offset: 0.14 }),
          style({ transform: 'scale(1)', offset: 0.28 }),
          style({ transform: 'scale(1.15)', offset: 0.42 }),
          style({ transform: 'scale(1)', offset: 0.70 })
        ]))
      ])
    ]),
    trigger('slideIn', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateX(-30px)' }),
        animate('0.8s 0.3s cubic-bezier(0.35, 0, 0.25, 1)', 
          style({ opacity: 1, transform: 'translateX(0)' }))
      ])
    ])
  ]
})
export class AppComponent implements OnInit {
  showContent = false;
  heartState = 'normal';
  sparkles: number[] = Array(20).fill(0).map((_, i) => i);
  currentYear: number = new Date().getFullYear();

  messages = [
    "You make my heart skip a beat! ðŸ’—",
    "Every moment with you is magical âœ¨",
    "You're the reason I smile every day ðŸ˜Š",
    "Together is my favorite place to be ðŸŒŸ",
    "You mean the world to me ðŸŒ",
    "My heart beats for you ðŸ’“"
  ];

  currentMessageIndex = 0;
  currentMessage = '';

  ngOnInit() {
    setTimeout(() => {
      this.showContent = true;
      this.startHeartBeat();
      this.rotateMessages();
    }, 300);
  }

  startHeartBeat() {
    setInterval(() => {
      this.heartState = 'beat';
      setTimeout(() => {
        this.heartState = 'normal';
      }, 1200);
    }, 3000);
  }

  rotateMessages() {
    this.currentMessage = this.messages[this.currentMessageIndex];
    setInterval(() => {
      this.currentMessageIndex = (this.currentMessageIndex + 1) % this.messages.length;
      this.currentMessage = this.messages[this.currentMessageIndex];
    }, 4500);
  }

  onLoveClick() {
    // Create confetti effect
    this.createConfetti();
    alert('I love you too! ðŸ’–');
  }

  createConfetti() {
    // This would trigger confetti animation
    const card = document.querySelector('.card');
    if (card) {
      card.classList.add('celebrate');
      setTimeout(() => {
        card?.classList.remove('celebrate');
      }, 1000);
    }
  }
}
