import { Component, OnInit } from '@angular/core';
import { trigger, state, style, transition, animate, keyframes } from '@angular/animations';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  animations: [
    trigger('fadeIn', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(50px) scale(0.9)' }),
        animate('1.2s cubic-bezier(0.68, -0.55, 0.265, 1.55)', 
                style({ opacity: 1, transform: 'translateY(0) scale(1)' }))
      ])
    ]),
    trigger('heartBeat', [
      state('beat', style({ transform: 'scale(1)' })),
      transition('* => beat', [
        animate('1s', keyframes([
          style({ transform: 'scale(1)', offset: 0 }),
          style({ transform: 'scale(1.4)', offset: 0.14 }),
          style({ transform: 'scale(1)', offset: 0.28 }),
          style({ transform: 'scale(1.4)', offset: 0.42 }),
          style({ transform: 'scale(1)', offset: 0.70 })
        ]))
      ])
    ])
  ]
})
export class AppComponent implements OnInit {
  showContent = false;
  heartState = 'normal';

  messages = [
    "You make my heart skip a beat! üíó",
    "Every moment with you is magical ‚ú®",
    "You're the reason I smile every day üòä",
    "I'm so lucky to have you in my life üçÄ",
    "You mean the world to me üåç",
    "Your love makes everything beautiful üå∏",
    "You're my favorite person ever üíï",
    "Being with you feels like home üè°",
    "You make my dreams come true üåü",
    "Forever grateful for your love üôè"
  ];

  currentMessageIndex = 0;
  currentMessage = '';

  ngOnInit() {
    setTimeout(() => {
      this.showContent = true;
      this.startHeartBeat();
      this.rotateMessages();
    }, 500);
  }

  startHeartBeat() {
    setInterval(() => {
      this.heartState = 'beat';
      setTimeout(() => {
        this.heartState = 'normal';
      }, 1000);
    }, 2500);
  }

  rotateMessages() {
    this.currentMessage = this.messages[this.currentMessageIndex];
    setInterval(() => {
      this.currentMessageIndex = (this.currentMessageIndex + 1) % this.messages.length;
      this.currentMessage = this.messages[this.currentMessageIndex];
    }, 4500);
  }

  showLoveMessage() {
    const loveMessages = [
      "I love you too! üíñ",
      "You make me so happy! üòç",
      "You're my everything! üíù",
      "Forever and always! üíï",
      "You're amazing! ‚ú®"
    ];
    const randomMessage = loveMessages[Math.floor(Math.random() * loveMessages.length)];
    alert(randomMessage);
  }
}
